import React, { useState } from 'react';
import './login.css';

const Login = ({ onSwitch }) => {
  const [email, setEmail] = useState('');
  const [passwort, setPasswort] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch('http://localhost:5000/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, passwort })
      });

      const data = await res.json();
      if (res.ok) {
        alert(`Willkommen, ${data.user.vorname}`);
        // hier später: Weiterleitung oder Speicherung des Tokens
      } else {
        alert(data.error || 'Fehler beim Login');
      }
    } catch (err) {
      console.error(err);
      alert('Serverfehler');
    }
  };

  return (
    <div className="login-wrapper">
      <header className="logo">
        <img src="/pauly_logo4.png" alt="Pauly Logo" />
      </header>
      <div className="overlay">
        <form onSubmit={handleLogin}>
          <div className="con">
            <header className="head-form">
              <h2>Anmelden</h2>
              <p>Bitte logge dich mit deinem Username/E-Mail und Passwort an</p>
            </header>

            <div className="field-set">
              <input className="form-input" type="email" placeholder="E-Mail" value={email} onChange={e => setEmail(e.target.value)} required />
              <input className="form-input" type="password" placeholder="Passwort" value={passwort} onChange={e => setPasswort(e.target.value)} required />
              <button type="submit" className="log-in">Anmelden</button>
            </div>

            <div className="other">
              <button type="button" className="btn submits frgt-pass">Passwort vergessen</button>
              <button type="button" className="btn submits sign-up" onClick={onSwitch}>Registrieren</button>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Login;
