import React, { useState } from 'react';
import './register.css';

const Register = ({ onSwitch }) => {
  const [formData, setFormData] = useState({
    vorname: '',
    nachname: '',
    email: '',
    passwort: ''
  });

  const handleChange = (e) => {
    setFormData(prev => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch('http://localhost:5000/api/auth/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });

      const data = await res.json();
      if (res.ok) {
        alert('Registrierung erfolgreich!');
        onSwitch(); // zur端ck zum Login
      } else {
        alert(data.error || 'Fehler bei der Registrierung');
      }
    } catch (err) {
      console.error(err);
      alert('Serverfehler');
    }
  };

  return (
    <div className="login-wrapper">
      <header className="logo">
        <img src="/pauly_logo4.png" alt="Pauly Logo" />
      </header>
      <div className="overlay">
        <form onSubmit={handleSubmit}>
          <div className="con">
            <header className="head-form">
              <h2>Registrieren</h2>
              <p>Bitte f端lle alle Felder aus, um ein neues Konto zu erstellen</p>
            </header>

            <div className="field-set">
              <input className="form-input" type="text" placeholder="Vorname" name="vorname" required onChange={handleChange} />
              <input className="form-input" type="text" placeholder="Nachname" name="nachname" required onChange={handleChange} />
              <input className="form-input" type="email" placeholder="E-Mail" name="email" required onChange={handleChange} />
              <input className="form-input" type="password" placeholder="Passwort" name="passwort" required onChange={handleChange} />

              <button className="log-in">Registrieren</button>
            </div>

            <div className="other">
              <button type="button" className="btn submits log-in" onClick={onSwitch}>
                Zur端ck zum Login
              </button>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Register;
